1. Trimming # #chipseq_trimming.sh
#!/bin/bash # setting envs export bdir=&#34;/data3/projects/2022_KNU_EBV&#34; export hg38_bowtieidx=&#34;/data3/PUBLIC_DATA/ref_genomes/homo_sapiens/hg38/hg38_bowtie_idx/hg38.fa&#34; export hg38_bwaidx=&#34;/data3/PUBLIC_DATA/ref_genomes/homo_sapiens/hg38/hg38_bwa_index/hg38.fa&#34; export ebv_bowtie2idx=&#34;/data3/PUBLIC_DATA/ref_genomes/Human_gammaherpesvirus_4_EBV/EBV_bowtie2_idx/NC_007605.1.fa&#34; export ebv_bwaidx=&#34;/data3/PUBLIC_DATA/ref_genomes/Human_gammaherpesvirus_4_EBV/EBV_bwa_index/NC_007605.1.fa&#34; ### SET Path ### cd /data3/RAW_DATA/2023_KNU_EBV/ChIP-seq ### TRIMMING data ### mkdir -p trimmed sampdir=&#34;/data3/RAW_DATA/2023_KNU_EBV/ChIP-seq&#34; samplist=(&#34;Input&#34; &#34;p65&#34; &#34;RIgG&#34;) TRIMMOMATIC= &#34;/data/packages/trimmomatic/Trimmomatic-0.39/trimmomatic-0.39.jar&#34; for sampname in &#34;${samplist[@]}&#34;; do mkdir -p &#34;trimmed/${sampname}&#34; java -jar $TRIMMOMATIC PE -threads 40 -trimlog log1.txt $sampdir/${sampname}_1.fastq/${sampname}_1.fastq $sampdir/${sampname}_2.fastq/${sampname}_2.fastq $sampdir/trimmed/${sampname}/${sampname}_1.trimmed.fastq $sampdir/trimmed/${sampname}/${sampname}_1.up.trimmed.fastq $sampdir/trimmed/${sampname}/${sampname}_2.trimmed.fastq $sampdir/trimmed/${sampname}/${sampname}_2.up.trimmed.fastq ILLUMINACLIP:/data1/packages/trimmomatic/Trimmomatic-0.39/adapters/TruSeq3-PE.fa:2:30:10:2:True LEADING:3 TRAILING:3 MINLEN:36 done 2. Alignment # #chipseq_alignment.sh
#!/bin/bash # setting envs export bdir=&#34;/data3/projects/2022_KNU_EBV&#34; export hg38_bowtieidx=&#34;/data3/PUBLIC_DATA/ref_genomes/homo_sapiens/hg38/hg38_bowtie_idx/hg38.fa&#34; export hg38_bwaidx=&#34;/data3/PUBLIC_DATA/ref_genomes/homo_sapiens/hg38/hg38_bwa_index/hg38.fa&#34; export ebv_bowtie2idx=&#34;/data3/PUBLIC_DATA/ref_genomes/Human_gammaherpesvirus_4_EBV/EBV_bowtie2_idx/NC_007605.1.fa&#34; export ebv_bwaidx=&#34;/data3/PUBLIC_DATA/ref_genomes/Human_gammaherpesvirus_4_EBV/EBV_bwa_index/NC_007605.1.fa&#34; ### SET Path ### cd /data3/RAW_DATA/2023_KNU_EBV/ChIP-seq sampdir=&#34;/data3/RAW_DATA/2023_KNU_EBV/ChIP-seq&#34; samplist=(&#34;Input&#34; &#34;p65&#34; &#34;RIgG&#34;) for sampname in &#34;${samplist[@]}&#34;; do bwa mem -t 20 -v 2 $ebv_bwaidx $bdir/trimmed/CTCF-C-SNU719_1.trimmed.fastq $bdir/trimmed/CTCF-C-SNU719_2.trimmed.fastq &gt; $bdir/aln/bwa/CTCF-C_PE.bwa.sam done ### Aligning to EBV - PE bwa mem -t 20 -v 2 $ebv_bwaidx $bdir/trimmed/CTCF-C-SNU719_1.trimmed.fastq $bdir/trimmed/CTCF-C-SNU719_2.trimmed.fastq &gt; $bdir/aln/bwa/CTCF-C_PE.bwa.sam samtools view -hf 2 $bdir/aln/bwa/CTCF-C_PE.bwa.sam | samtools sort -o $bdir/aln/bwa/CTCF-C_PE.bwa.bam -O BAM -@ 20 - samtools index -@ 20 $bdir/aln/bwa/CTCF-C_PE.bwa.bam bamCoverage -b $bdir/aln/bwa/CTCF-C_PE.bwa.bam -o $bdir/aln/bwa/CTCF-C_PE.bwa.bam.bigwig macs2 callpeak -t $bdir/aln/bwa/CTCF-C_PE.bwa.bam -f BAMPE -n CTCF-C --outdir peaks_ebv ### @REF: hg38 bowtie2 -k1 --no-unal -p 40 --qc-filter -x $bowtie2idx_hg38 -1 $bdir/trimmed/CTCF-C-SNU719_1.trimmed.fastq -2 $bdir/trimmed/CTCF-C-SNU719_2.trimmed.fastq &gt; $bdir/aln/bwt2/CTCF-C_PE_hg38.sam &gt; $bdir/aln/bwt2/CTCF-C_PE_hg38.sam samtools view -hf 2 $bdir/aln/bwt2/CTCF-C_PE_hg38.sam | samtools sort -o $bdir/aln/bwt2/CTCF-C_PE_hg38.bam -O BAM -@ 20 - sambamba view -h -t 20 -f bam -p -F &#34;[XS] == null and not unmapped and not duplicate&#34; $bdir/aln/bwt2/CTCF-C_PE_hg38.bam &gt; $bdir/aln/bwt2/CTCF-C_PE_hg38_u.bam samtools index -@ 20 $bdir/aln/bwt2/CTCF-C_PE_hg38_u.bam macs2 callpeak -t $bdir/aln/bwt2/CTCF-C_PE_hg38_u.bam -f BAMPE -n CTCF-C --outdir peaks 3. Peak Calling # #bedgraph.sh
#!/bin/bash input_file=&#34;KEB01_1_bismark_bt2_pe.sorted.bedGraph.gz&#34; output_file=&#34;KEB01_1_bismark_bt2_pe.sorted_edited.bedGraph&#34; cd /data/home/ysh980101/2309_5-aza/Bismark_temp_GRCh38 zcat &#34;$input_file&#34; | awk &#39;$4 != 0 { $1 = &#34;chr&#34; $1; print }&#39; | gzip &gt; &#34;$output_file.gz&#34;
